function framesToMs(_frames) {
    if (Number.isInteger(_frames)) {
        return 1000/60*_frames;
    } else {
        return false;
    };
}

function generateRandomID(_g=false) {
    let generatedID = '';
    let maxC = _g ? 16 : 8;
    for (let i=0; i<maxC; i++) {
        generatedID +=~~((Math.random()*9)+1).toString();
    }
    return generatedID;
}

function generateUserID() { // gens UIDs format [AZ09]*1_[AZ09]*2_[AZ09]*4_[AZ09]*8_[AZ09]*16
    let available = Phaser.Utils.Array.NumberArray(0,9);
    let o = 32;
    for (let c=33; c<33+o-6; c++) {
        available.push(String.fromCharCode(c+o));
    };

    let ints = [];
    [540541546547,176178179].forEach((_int)=> {
        let a = _int.toString();
        while (a) {
            let char = a.slice(-3);
            a=a.replace(char,'');
            ints.push(~~char);
        };
    }); ints.reverse();

    ints.forEach((_int)=> {
        let m=2;
        while(m) { available.push(String.fromCharCode(_int)); m--; }; // double up on the special characters
    });

    let UID = '';
    let sections = [1,2,3,4,5];
    sections.forEach((_r)=> {
        let r=Math.pow(2,_r-1);
        while(r) {
            let rC = getRandom(available);
            UID+=rC;
            r--;
        }
        _r<sections.length ? UID+='_' : null;
    })

    return UID;
}

function getStringExtForInt(_int) {
    if (!Number.isInteger(_int)) return false;

    let str = _int.toString();
    let ext = 'th';
    if (str.substr(-1)==='1' && _int!==11) {
        ext = 'st';
    } else if (str.substr(-1)==='2' && _int!==12) {
        ext = 'nd';
    } else if (str.substr(-1)==='3' && _int!==13) {
        ext = 'rd';
    }
    return ext;
}

function isVar(searchFor='Phaser') {
    let message = '';
    if (searchFor==='Phaser') { message = 'No variable passed... Searching for Phaser variable\n'; }
    for(var q in window) { 
        if(window.hasOwnProperty(q) && q===searchFor) {
            message += `Found the variable '${searchFor}'`;
            vars.DEBUG ? console.log(message) : null;
            return true;
        }
    }
    return false;
}

function shuffle(array) {
    var currentIndex = array.length, temporaryValue, randomIndex;
    while (0 !== currentIndex) {
        randomIndex = Math.floor(Math.random() * currentIndex);
        currentIndex -= 1;
        temporaryValue = array[currentIndex];
        array[currentIndex] = array[randomIndex];
        array[randomIndex] = temporaryValue;
    }
    return array;
}

function getRandom(_min,_max=null) { // this function can deal with a min/max, array or a string
    if (!Phaser) { console.error(`This function needs Phaser to be initialised before use!`); return false; }

    if (Array.isArray(_min)) {
        return Phaser.Math.RND.pick(_min);
    } else {
        if (typeof _min==='number' && typeof _max==='number') {
            return Phaser.Math.RND.between(_min,_max);
        } else if (typeof _min==='string' && _max===null) { // string has been passed 
            return Phaser.Math.RND.pick(_min.split(''));
        } else {
            console.error('The first passed var must either be an array, integer or string. If a 2nd value is passed it must be an integer');
        }
    }
}

